// src/redux/store.js
import { configureStore } from '@reduxjs/toolkit';
import notesReducer from './slices/notesSlice'; // Исправленный путь
import authReducer from './slices/authSlice';

export const store = configureStore({
  reducer: {
    notes: notesReducer,
  },
  reducer: {
    auth: authReducer,
  },
});
