import React, { useState } from 'react';
import { Provider } from 'react-redux';
import { store } from './redux/store';
import AppRouter from './router/AppRouter'; // Роутер без BrowserRouter
import './App.css'; // Добавьте стили, если нужно
import newLogo from './assets/logo.png';
import NoteList from './components/NoteList'; // Импортируем компонент списка заметок

// Основной компонент приложения
const App = () => {
  const [notes, setNotes] = useState([
    // Пример заметок с ID, темой и тегами
    { id: 1, title: 'Первая заметка', tags: ['работа', 'важно'] },
    { id: 2, title: 'Вторая заметка', tags: ['дом', 'не забыть'] },
  ]);

  const handleDeleteNote = (id) => {
    setNotes(notes.filter((note) => note.id !== id));
  };

  const handleViewNote = (id) => {
    alert(`Открыть заметку с ID ${id}`); // Пример открытия заметки
  };

  return (
    <Provider store={store}>
      <div className="App">
        <header className="App-header">
          <div className="logo-container">
            <img src={newLogo} className="App-logo" alt="new logo" />
          </div>
          {/* Роутер с маршрутами */}
          <AppRouter />
        </header>

        {/* Компонент для отображения списка заметок */}
        <NoteList notes={notes} onDeleteNote={handleDeleteNote} onViewNote={handleViewNote} />
      </div>
    </Provider>
  );
};

export default App;
