import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { restoreNote, deleteNoteForever } from '../redux/slices/notesSlice';
import { Link } from 'react-router-dom';

const TrashPage = () => {
  const dispatch = useDispatch();
  const trashNotes = useSelector((state) => state.notes.trash);

  const handleRestore = (noteId) => {
    dispatch(restoreNote(noteId)); // Восстановление заметки
  };

  const handleDeleteForever = (noteId) => {
    dispatch(deleteNoteForever(noteId)); // Удаление заметки навсегда
  };

  return (
    <div className="trash-page">
      <h2>Корзина</h2>
      {trashNotes.length === 0 ? (
        <p>Корзина пуста.</p>
      ) : (
        trashNotes.map((note) => (
          <div key={note.id} className="trash-note-item">
            <div className="note-header">
              <h3>{note.topic}</h3>
              <div>{note.createdAt}</div>
            </div>
            <div className="note-body">{note.text}</div>
            <div className="note-footer">
              <span>{note.tag}</span>
              <button onClick={() => handleRestore(note.id)}>Восстановить</button>
              <button onClick={() => handleDeleteForever(note.id)}>Удалить навсегда</button>
            </div>
          </div>
        ))
      )}
      <div className="navigation-links">
        <Link to="/">Перейти на главную страницу</Link>
      </div>
    </div>
  );
};

export default TrashPage;
