import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import '../styles/HomePage.css';

const HomePage = () => {
  const [notes, setNotes] = useState([]);
  const [newNote, setNewNote] = useState({ text: '', topic: '', tag: '' });
  const [popupNote, setPopupNote] = useState(null);

  const handleAddNote = () => {
    if (newNote.topic && newNote.text) {
      setNotes([...notes, newNote]);
      setNewNote({ text: '', topic: '', tag: '' });
    }
  };

  const handleDeleteNote = (index) => {
    setNotes(notes.filter((_, i) => i !== index));
  };

  const handleViewNote = (note) => {
    setPopupNote(note);
  };

  const handleTextChange = (e) => {
    if (e.target.value.length <= 200) {
      setNewNote({ ...newNote, text: e.target.value });
    }
  };

  return (
    <div className="home-page">
      {notes.length === 0 && <div className="centered-text">Жду ваши заметки!</div>}

      <div className="note-form">
        <input
          type="text"
          value={newNote.topic}
          onChange={(e) => setNewNote({ ...newNote, topic: e.target.value })}
          placeholder="Тема заметки"
          className="note-input"
        />
        <input
          type="text"
          value={newNote.tag}
          onChange={(e) => setNewNote({ ...newNote, tag: e.target.value })}
          placeholder="Тег"
          className="note-input"
        />
        <textarea
          value={newNote.text}
          onChange={handleTextChange}
          placeholder="Текст заметки (не более 200 символов)"
          className="note-input"
        />
        <div className="char-counter">{newNote.text.length}/200</div>
        <button onClick={handleAddNote} className="note-button">
          Записать
        </button>
      </div>

      {popupNote && (
        <div className="popup">
          <div className="popup-content">
            <h3>{popupNote.topic}</h3>
            <p>
              <strong>Тег:</strong> {popupNote.tag}
            </p>
            <p>{popupNote.text}</p>
            <button onClick={() => setPopupNote(null)} className="popup-close">
              Закрыть
            </button>
          </div>
        </div>
      )}

      <div className="notes-container">
        {notes.map((note, index) => (
          <div className="note" key={index}>
            <div className="note-header">
              <span>
                <strong>{note.topic}</strong> - {note.tag}
              </span>
              <button onClick={() => handleDeleteNote(index)} className="delete-note">
                Удалить
              </button>
            </div>
            <button onClick={() => handleViewNote(note)} className="view-note">
              Смотреть
            </button>
          </div>
        ))}
      </div>

      <div className="calendar-container">
        <Calendar />
      </div>
    </div>
  );
};

export default HomePage;
