// src/pages/HomePage.js
import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css'; // Импорт стилей для календаря
import newLogo from '../assets/logo.png'; // Путь к новому логотипу

const HomePage = () => {
  const [date, setDate] = useState(new Date());

  const onChange = (newDate) => setDate(newDate);

  return (
    <div style={styles.container}>
      {/* Логотип слева сверху */}
      <div style={styles.logoContainer}>
        <img src={newLogo} alt="Logo" style={styles.logo} />
      </div>

      {/* Календарь справа снизу */}
      <div style={styles.calendarContainer}>
        <Calendar onChange={onChange} value={date} />
      </div>
    </div>
  );
};

// Стили для компонентов
const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    height: '100vh', // Полный экран
    justifyContent: 'flex-start',
    alignItems: 'flex-start',
    position: 'relative',
    padding: '20px',
    backgroundColor: '#2d2d2d',
    color: '#fff',
  },
  logoContainer: {
    marginBottom: '20px',
  },
  logo: {
    width: '100px',
    height: 'auto',
  },
  calendarContainer: {
    position: 'absolute',
    bottom: '20px',
    right: '20px',
  },
};

export default HomePage;
