// components/NoteList.js
import React from 'react';

const Note = ({ note, onDeleteNote, onViewNote }) => {
  return (
    <div className="note" key={note.id}>
      <div className="note-header">
        <span className="note-id">Заметка {note.id}</span> {/* Отображаем ID заметки */}
        <button
          className="delete-note"
          onClick={() => onDeleteNote(note.id)}
        >
          Удалить
        </button>
      </div>
      <div className="note-body">
        <div className="note-title">{note.title}</div> {/* Тема */}
        <div className="note-tags">{note.tags.join(', ')}</div> {/* Теги */}
        <div className="note-dates">
          <p><strong>Дата начала:</strong> {new Date(note.startDate).toLocaleString()}</p>
          <p><strong>Дата окончания:</strong> {new Date(note.endDate).toLocaleString()}</p>
        </div>
        <button
          className="view-note"
          onClick={() => onViewNote(note.id)}
        >
          Смотреть
        </button>
      </div>
    </div>
  );
};

const NoteList = ({ notes, onDeleteNote, onViewNote }) => {
  return (
    <div className="notes-container">
      {notes.map((note) => (
        <Note
          key={note.id}
          note={note}
          onDeleteNote={onDeleteNote}
          onViewNote={onViewNote}
        />
      ))}
    </div>
  );
};

export default NoteList;
