// src/pages/HomePage.js
import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css'; // Импортируем стили для календаря
import '../styles/HomePage.css'; // Подключаем стили для страницы

const HomePage = () => {
  const [showCalendar, setShowCalendar] = useState(false);

  const toggleCalendar = () => {
    setShowCalendar(!showCalendar);
  };

  return (
    <div className="home-page">
      {/* Логотип в левом верхнем углу */}
      <div className="logo-container">
        <img src={require('../assets/logo.png')} alt="logo" className="App-logo" />
      </div>

      {/* Надпись "Жду ваши заметки!" */}
      <div className="centered-text">
        Жду ваши заметки!
      </div>

      {/* Форма для записи заметки */}
      <div className="note-form">
        <input type="text" placeholder="Тема" className="note-input" />
        <input type="text" placeholder="Тег" className="note-input" />
        <textarea placeholder="Текст заметки" className="note-input" />
        <button className="note-button">Записать</button>
      </div>

      {/* Кнопка для вызова календаря на мобильных устройствах */}
      <button className="calendar-button" onClick={toggleCalendar}>
        Открыть календарь
      </button>

      {/* Попап с календарем */}
      {showCalendar && (
        <div className="popup">
          <div className="popup-content">
            <Calendar />
            <button className="popup-close" onClick={toggleCalendar}>Закрыть</button>
          </div>
        </div>
      )}

      {/* Сама заметка */}
      <div className="notes-container">
        <div className="note">
          <div className="note-header">
            <span>Тема заметки</span>
            <button className="delete-note">Удалить</button>
          </div>
          <p>Текст заметки</p>
          <button className="view-note">Посмотреть</button>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
