import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  notes: [], // список заметок
  trash: [], // список удалённых заметок (для корзины)
};

const notesSlice = createSlice({
  name: 'notes',
  initialState,
  reducers: {
    addNote: (state, action) => {
      state.notes.push(action.payload);
    },
    deleteNote: (state, action) => {
      const noteIndex = state.notes.findIndex(note => note.id === action.payload);
      if (noteIndex !== -1) {
        state.trash.push(state.notes[noteIndex]);
        state.notes.splice(noteIndex, 1);
      }
    },
    setNotes: (state, action) => {
      state.notes = action.payload;
    },
    restoreNote: (state, action) => {
      const noteIndex = state.trash.findIndex(note => note.id === action.payload);
      if (noteIndex !== -1) {
        state.notes.push(state.trash[noteIndex]);
        state.trash.splice(noteIndex, 1);
      }
    },
    deleteNoteForever: (state, action) => {
      const noteIndex = state.trash.findIndex(note => note.id === action.payload);
      if (noteIndex !== -1) {
        state.trash.splice(noteIndex, 1); // Удаляем навсегда
      }
    },
  },
});

export const { addNote, deleteNote, setNotes, restoreNote, deleteNoteForever } = notesSlice.actions;

export default notesSlice.reducer;
