// App.js
import React, { useState } from 'react';
import { Provider } from 'react-redux';
import { store } from './redux/store';
import AppRouter from './router/AppRouter'; // Роутер без BrowserRouter
import './App.css'; // Добавьте стили, если нужно
import newLogo from './assets/logo.png';
import NoteForm from './components/NoteForm'; // Импортируем форму создания заметки
import NoteList from './components/NoteList'; // Импортируем компонент для отображения списка заметок

const App = () => {
  const [notes, setNotes] = useState([]);

  const handleAddNote = (newNote) => {
    setNotes((prevNotes) => [...prevNotes, newNote]);
  };

  const handleDeleteNote = (id) => {
    setNotes((prevNotes) => prevNotes.filter((note) => note.id !== id));
  };

  const handleViewNote = (id) => {
    const note = notes.find((note) => note.id === id);
    if (note) {
      alert(`Заметка: ${note.title}\nТеги: ${note.tags.join(', ')}\nДата начала: ${new Date(note.startDate).toLocaleString()}\nДата окончания: ${new Date(note.endDate).toLocaleString()}`);
    }
  };

  return (
    <Provider store={store}>
      <div className="App">
        <header className="App-header">
          <div className="logo-container">
            <img src={newLogo} className="App-logo" alt="new logo" />
          </div>
          <AppRouter />
        </header>

        {/* Форма для добавления заметки */}
        <NoteForm onAddNote={handleAddNote} />

        {/* Список заметок */}
        <NoteList 
          notes={notes} 
          onDeleteNote={handleDeleteNote} 
          onViewNote={handleViewNote} 
        />
      </div>
    </Provider>
  );
};

export default App;
