// src/redux/slices/notesSlice.js
import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  notes: [],
  deletedNotes: [],
};

const notesSlice = createSlice({
  name: 'notes',
  initialState,
  reducers: {
    addNote: (state, action) => {
      state.notes.push(action.payload);
    },
    deleteNote: (state, action) => {
      const noteIndex = state.notes.findIndex((note) => note.id === action.payload);
      if (noteIndex !== -1) {
        const [deletedNote] = state.notes.splice(noteIndex, 1);
        state.deletedNotes.push(deletedNote);
      }
    },
    restoreNote: (state, action) => {
      const noteIndex = state.deletedNotes.findIndex((note) => note.id === action.payload.id);
      if (noteIndex !== -1) {
        const [restoredNote] = state.deletedNotes.splice(noteIndex, 1);
        state.notes.push(restoredNote);
      }
    },
    deleteNoteForever: (state, action) => {
      const noteIndex = state.deletedNotes.findIndex((note) => note.id === action.payload);
      if (noteIndex !== -1) {
        state.deletedNotes.splice(noteIndex, 1);
      }
    },
  },
});

export const { addNote, deleteNote, restoreNote, deleteNoteForever } = notesSlice.actions;

export default notesSlice.reducer;
