import React, { useState, useEffect } from 'react';

const NoteForm = ({ onAdd, onSaveEdit, editNote }) => {
  const [newNote, setNewNote] = useState({ text: '', topic: '', tag: '' });

  useEffect(() => {
    if (editNote) {
      setNewNote({ text: editNote.text, topic: editNote.topic, tag: editNote.tag });
    }
  }, [editNote]);

  const handleChange = (e) => {
    setNewNote({ ...newNote, [e.target.name]: e.target.value });
  };

  const handleSubmit = () => {
    if (editNote) {
      onSaveEdit(newNote);
    } else {
      onAdd(newNote);
    }
    setNewNote({ text: '', topic: '', tag: '' });
  };

  return (
    <div className="note-form">
      <input
        type="text"
        name="topic"
        value={newNote.topic}
        onChange={handleChange}
        placeholder="Тема заметки"
        className="note-input"
      />
      <input
        type="text"
        name="tag"
        value={newNote.tag}
        onChange={handleChange}
        placeholder="Тег"
        className="note-input"
      />
      <textarea
        name="text"
        value={newNote.text}
        onChange={handleChange}
        placeholder="Текст заметки"
        className="note-input"
      />
      <button onClick={handleSubmit} className="note-button">
        {editNote ? 'Сохранить изменения' : 'Записать'}
      </button>
    </div>
  );
};

export default NoteForm;
