// src/components/NoteList.js
import React from 'react';

const NoteList = ({ notes, onViewNote, onDeleteNote }) => {
  return (
    <div className="notes-container">
      {notes.map((note) => (
        <div className="note" key={note.id}>
          <div className="note-header">
            <span className="note-id">Заметка {note.id}</span> {/* Отображаем ID */}
            <button
              className="delete-note"
              onClick={() => onDeleteNote(note.id)}
            >
              Удалить
            </button>
          </div>
          <div className="note-body">
            <div className="note-title">{note.title}</div> {/* Тема */}
            <div className="note-tags">{note.tags.join(', ')}</div> {/* Теги */}
            <button
              className="view-note"
              onClick={() => onViewNote(note.id)}
            >
              Смотреть
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default NoteList;
