import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { logout } from '../redux/slices/authSlice';
import { addNote, deleteNote, updateNote } from '../redux/slices/notesSlice';
import { useNavigate } from 'react-router-dom';
import NoteForm from './NoteForm';
import CalendarComponent from './CalendarComponent';
import NotesList from './NotesList';

const HomePage = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const username = useSelector((state) => state.auth.username);
  const notes = useSelector((state) => state.notes.notes);
  const [editNote, setEditNote] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedDate, setSelectedDate] = useState(null);

  const handleAddNote = (note) => {
    const newNoteWithDate = { ...note, createdAt: new Date().toISOString() };
    dispatch(addNote(newNoteWithDate));
  };

  const handleDeleteNote = (noteId) => {
    dispatch(deleteNote(noteId));
  };

  const handleEditNote = (note) => {
    setEditNote(note);
  };

  const handleSaveEdit = (updatedNote) => {
    dispatch(updateNote(updatedNote));
    setEditNote(null);
  };

  const handleLogout = () => {
    dispatch(logout());
  };

  const handleGoToTrash = () => {
    navigate('/trash');
  };

  const filteredNotes = notes.filter((note) => {
    const searchInText = note.text.toLowerCase().includes(searchQuery);
    const searchInTopic = note.topic.toLowerCase().includes(searchQuery);
    const searchInTag = note.tag.toLowerCase().includes(searchQuery);
    return searchInText || searchInTopic || searchInTag;
  });

  const filteredByDate = selectedDate
    ? filteredNotes.filter((note) => {
        const noteDate = new Date(note.createdAt);
        return (
          noteDate.getFullYear() === selectedDate.getFullYear() &&
          noteDate.getMonth() === selectedDate.getMonth() &&
          noteDate.getDate() === selectedDate.getDate()
        );
      })
    : filteredNotes;

  const sortedNotes = filteredByDate.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));

  return (
    <div className="home-page">
      <div className="profile-container">
        <span className="username">{username}</span>
        <button onClick={handleLogout} className="logout-button">Выйти</button>
        <button onClick={handleGoToTrash} className="go-to-trash-button">Корзина</button>
      </div>

      {notes.length === 0 && <div className="centered-text">Жду ваши заметки!</div>}

      <div className="search-bar">
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value.toLowerCase())}
          placeholder="Поиск по ключевым словам"
          className="search-input"
        />
      </div>

      <NoteForm onAdd={handleAddNote} onSaveEdit={handleSaveEdit} editNote={editNote} />
      <CalendarComponent selectedDate={selectedDate} setSelectedDate={setSelectedDate} />
      <NotesList notes={sortedNotes} onDelete={handleDeleteNote} onEdit={handleEditNote} />
    </div>
  );
};

export default HomePage;
