// src/pages/HomePage.js
import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css'; // Импортируем стили для календаря
import '../styles/HomePage.css'; // Подключаем стили для страницы

const HomePage = () => {
  const [notes, setNotes] = useState([]); // Состояние для заметок
  const [newNote, setNewNote] = useState({ text: '', topic: '', tag: '' }); // Состояние для новой заметки
  const [showPopup, setShowPopup] = useState(false); // Состояние для отображения попапа
  const [popupNote, setPopupNote] = useState(null); // Состояние для выбранной заметки в попапе

  // Функция для добавления новой заметки
  const handleAddNote = () => {
    if (newNote.topic.trim() === '') return; // Проверка на пустую тему
    setNotes([...notes, newNote]);
    setNewNote({ text: '', topic: '', tag: '' }); // Очищаем поля после добавления заметки
  };

  // Функция для удаления заметки
  const handleDeleteNote = (index) => {
    setNotes(notes.filter((_, i) => i !== index));
  };

  // Открытие попапа с заметкой
  const handleOpenNote = (note) => {
    setPopupNote(note);
    setShowPopup(true);
  };

  // Функция для ограничения длины темы
  const handleTopicChange = (e) => {
    const topic = e.target.value.slice(0, 30); // Ограничение на 30 символов
    setNewNote({ ...newNote, topic });
  };

  // Функция для ограничения длины тега
  const handleTagChange = (e) => {
    const tag = e.target.value.slice(0, 30); // Ограничение на 30 символов
    setNewNote({ ...newNote, tag });
  };

  // Функция для изменения текста заметки
  const handleTextChange = (e) => {
    setNewNote({ ...newNote, text: e.target.value });
  };

  return (
    <div className="home-page">
      {/* Если нет заметок, показываем сообщение */}
      {notes.length === 0 && (
        <div className="centered-text">Жду ваши заметки!</div>
      )}

      {/* Форма для создания новой заметки */}
      <div className="note-form">
        <input
          type="text"
          value={newNote.topic}
          onChange={handleTopicChange} // Используем обработчик с ограничением
          placeholder="Тема заметки"
          className="note-input"
        />
        <input
          type="text"
          value={newNote.tag}
          onChange={handleTagChange} // Используем обработчик с ограничением
          placeholder="Тег"
          className="note-input"
        />
        <textarea
          value={newNote.text}
          onChange={handleTextChange}
          placeholder="Текст заметки"
          className="note-input"
        />
        <button onClick={handleAddNote} className="note-button">Записать</button>
      </div>

      {/* Попап для отображения текста заметки */}
      {showPopup && popupNote && (
        <div className="popup">
          <div className="popup-content">
            <div>
              <strong>Тема:</strong> {popupNote.topic}
            </div>
            <div>
              <strong>Теги:</strong> {popupNote.tag}
            </div>
            <div>
              <strong>Текст:</strong> <p>{popupNote.text}</p>
            </div>
            <button onClick={() => setShowPopup(false)} className="popup-close">Закрыть</button>
          </div>
        </div>
      )}

      {/* Отображаем заметки, если они есть */}
      <div className="notes-container">
        {notes.map((note, index) => (
          <div className="note" key={index}>
            <div className="note-header">
              <span>{note.topic} - {note.tag}</span>
              <button
                onClick={() => handleDeleteNote(index)}
                className="delete-note"
              >
                Удалить
              </button>
            </div>
            <p>{note.text.substring(0, 30)}...</p> {/* Ограничиваем длину текста */}
            <button
              onClick={() => handleOpenNote(note)}
              className="view-note"
            >
              Смотреть
            </button>
          </div>
        ))}
      </div>

      {/* Календарь */}
      <div className="calendar-container">
        <Calendar />
      </div>
    </div>
  );
};

export default HomePage;
