import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { updateNote, deleteNote, addNote } from '../redux/slices/notesSlice'; // Экшены для заметок
import { logout } from '../redux/slices/authSlice'; // Экшен для выхода
import { useNavigate } from 'react-router-dom';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import '../styles/HomePage.css';

const HomePage = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const username = useSelector((state) => state.auth.username);
  const [newNote, setNewNote] = useState({ text: '', topic: '', tag: '' });
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedDate, setSelectedDate] = useState(null);
  const [editingNote, setEditingNote] = useState(null);
  const notes = useSelector((state) => state.notes.notes); // Берем заметки из Redux

  const handleAddNote = () => {
    if (newNote.topic.trim() === '') return;
    const newNoteWithDate = { ...newNote, createdAt: new Date().toISOString(), id: Date.now().toString() };
    dispatch(addNote(newNoteWithDate)); // Добавляем заметку через Redux
    setNewNote({ text: '', topic: '', tag: '' });
  };

  const handleDeleteNote = (noteId) => {
    dispatch(deleteNote(noteId)); // Удаляем заметку через Redux
  };

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value.toLowerCase());
  };

  const handleUpdateNote = () => {
    if (!editingNote) return;
    const updatedNote = { topic: newNote.topic, tag: newNote.tag, text: newNote.text };
    dispatch(updateNote({ id: editingNote.id, updatedNote }));
    setEditingNote(null);
    setNewNote({ text: '', topic: '', tag: '' });
  };

  const sortNotesByDate = (notesArray) => {
    return notesArray.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
  };

  const filteredNotes = notes.filter((note) => {
    const searchInText = note.text.toLowerCase().includes(searchQuery);
    const searchInTopic = note.topic.toLowerCase().includes(searchQuery);
    const searchInTag = note.tag.toLowerCase().includes(searchQuery);
    return searchInText || searchInTopic || searchInTag;
  });

  const sortedNotes = sortNotesByDate(filteredNotes);

  const handleLogout = () => {
    dispatch(logout()); // Выход из системы
  };

  const handleGoToTrash = () => {
    navigate('/trash');
  };

  const handleEditNote = (note) => {
    setEditingNote(note);
    setNewNote({ text: note.text, topic: note.topic, tag: note.tag });
  };

  return (
    <div className="home-page">
      <div className="profile-container">
        <span className="username">{username}</span>
        <button onClick={handleLogout} className="logout-button">Выйти</button>
        <button onClick={handleGoToTrash} className="go-to-trash-button">Корзина</button>
      </div>

      {notes.length === 0 && <div className="centered-text">Жду ваши заметки!</div>}

      <div className="search-bar">
        <input
          type="text"
          value={searchQuery}
          onChange={handleSearchChange}
          placeholder="Поиск по ключевым словам"
          className="search-input"
        />
      </div>

      <div className="note-form">
        <input
          type="text"
          value={newNote.topic}
          onChange={(e) => setNewNote({ ...newNote, topic: e.target.value })}
          placeholder="Тема заметки"
          className="note-input"
        />
        <input
          type="text"
          value={newNote.tag}
          onChange={(e) => setNewNote({ ...newNote, tag: e.target.value })}
          placeholder="Тег"
          className="note-input"
        />
        <textarea
          value={newNote.text}
          onChange={(e) => setNewNote({ ...newNote, text: e.target.value })}
          placeholder="Текст заметки"
          className="note-input"
        />
        <button onClick={editingNote ? handleUpdateNote : handleAddNote} className="note-button">
          {editingNote ? 'Обновить' : 'Записать'}
        </button>
      </div>

      <div className="notes-container">
        {sortedNotes.map((note) => (
          <div className="note" key={note.id}>
            <div className="note-header">
              <span>{note.topic} - {note.tag}</span>
              <button onClick={() => handleDeleteNote(note.id)} className="delete-note">Удалить</button>
              <button onClick={() => handleEditNote(note)} className="edit-note">Редактировать</button>
            </div>
            <p>{note.text.substring(0, 30)}...</p>
            <div className="note-date">
              {new Date(note.createdAt).toLocaleDateString()}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
