// src/pages/HomePage.js
import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css'; // Импортируем стили для календаря
import '../styles/HomePage.css'; // Подключаем стили для страницы

const HomePage = () => {
  const [notes, setNotes] = useState([]); // Состояние для заметок
  const [newNote, setNewNote] = useState({ text: '', topic: '', tag: '' }); // Состояние для новой заметки
  const [showPopup, setShowPopup] = useState(false); // Состояние для отображения попапа
  const [popupNote, setPopupNote] = useState(null); // Состояние для выбранной заметки в попапе
  const [editMode, setEditMode] = useState(false); // Режим редактирования
  const [currentNoteIndex, setCurrentNoteIndex] = useState(null); // Индекс редактируемой заметки
  const [editNote, setEditNote] = useState(null); // Состояние для редактируемой заметки в попапе
  const [searchQuery, setSearchQuery] = useState(''); // Состояние для строки поиска

  // Функция для добавления новой заметки
  const handleAddNote = () => {
    if (newNote.topic.trim() === '') return; // Проверка на пустую тему
    const newNoteWithDate = {
      ...newNote,
      createdAt: new Date().toISOString(), // Добавляем дату создания
    };
    setNotes([...notes, newNoteWithDate]);
    setNewNote({ text: '', topic: '', tag: '' }); // Очищаем поля после добавления заметки
  };

  // Функция для удаления заметки
  const handleDeleteNote = (index) => {
    setNotes(notes.filter((_, i) => i !== index));
  };

  // Открытие попапа с заметкой
  const handleOpenNote = (note, index) => {
    setPopupNote(note);
    setCurrentNoteIndex(index); // Сохраняем индекс заметки для редактирования
    setEditNote({ ...note }); // Инициализируем состояние для редактируемой заметки
    setShowPopup(true);
  };

  // Функция для начала редактирования заметки в попапе
  const handleEditNote = () => {
    setEditMode(true); // Включаем режим редактирования
  };

  // Функция для сохранения отредактированной заметки
  const handleSaveEdit = () => {
    const updatedNotes = [...notes];
    updatedNotes[currentNoteIndex] = {
      ...editNote, // Сохраняем изменения
      createdAt: popupNote.createdAt, // Сохраняем старую дату
    };
    setNotes(updatedNotes);
    setEditMode(false);
    setShowPopup(false); // Закрываем попап после сохранения
  };

  // Функция для обработки изменения текста заметки
  const handleTextChange = (e) => {
    setEditNote({ ...editNote, text: e.target.value }); // Меняем только для редактируемой заметки
  };

  // Функция для обработки изменения темы с ограничением в 30 символов
  const handleTopicChange = (e) => {
    const topic = e.target.value.slice(0, 30); // Ограничиваем до 30 символов
    setEditNote({ ...editNote, topic });
  };

  // Функция для обработки изменения тега с ограничением в 30 символов
  const handleTagChange = (e) => {
    const tag = e.target.value.slice(0, 30); // Ограничиваем до 30 символов
    setEditNote({ ...editNote, tag });
  };

  // Функция для обработки изменения строки поиска
  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value.toLowerCase()); // Переводим строку в нижний регистр для удобства поиска
  };

  // Функция для сортировки заметок по дате (по убыванию)
  const sortNotesByDate = (notesArray) => {
    return notesArray.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
  };

  // Фильтруем заметки по строке поиска
  const filteredNotes = notes.filter((note) => {
    const searchInText = note.text.toLowerCase().includes(searchQuery);
    const searchInTopic = note.topic.toLowerCase().includes(searchQuery);
    const searchInTag = note.tag.toLowerCase().includes(searchQuery);
    return searchInText || searchInTopic || searchInTag; // Ищем по всем полям
  });

  // Сортируем заметки по дате
  const sortedNotes = sortNotesByDate(filteredNotes);

  return (
    <div className="home-page">
      {/* Если нет заметок, показываем сообщение */}
      {notes.length === 0 && (
        <div className="centered-text">Жду ваши заметки!</div>
      )}

      {/* Строка поиска */}
      <div className="search-bar">
        <input
          type="text"
          value={searchQuery}
          onChange={handleSearchChange}
          placeholder="Поиск по ключевым словам"
          className="search-input"
        />
      </div>

      {/* Форма для создания новой заметки */}
      <div className="note-form">
        <input
          type="text"
          value={newNote.topic}
          onChange={(e) => setNewNote({ ...newNote, topic: e.target.value })}
          placeholder="Тема заметки"
          className="note-input"
        />
        <input
          type="text"
          value={newNote.tag}
          onChange={(e) => setNewNote({ ...newNote, tag: e.target.value })}
          placeholder="Тег"
          className="note-input"
        />
        <textarea
          value={newNote.text}
          onChange={(e) => setNewNote({ ...newNote, text: e.target.value })}
          placeholder="Текст заметки"
          className="note-input"
        />
        <button onClick={handleAddNote} className="note-button">Записать</button>
      </div>

      {/* Попап для отображения текста заметки */}
      {showPopup && popupNote && (
        <div className="popup">
          <div className="popup-content">
            <div>
              <strong>Тема:</strong>
              {editMode ? (
                <input
                  type="text"
                  value={editNote.topic}
                  onChange={handleTopicChange}
                  className="note-input"
                />
              ) : (
                <span>{popupNote.topic}</span>
              )}
            </div>
            <div>
              <strong>Теги:</strong>
              {editMode ? (
                <input
                  type="text"
                  value={editNote.tag}
                  onChange={handleTagChange}
                  className="note-input"
                />
              ) : (
                <span>{popupNote.tag}</span>
              )}
            </div>
            <div>
              <strong>Текст:</strong>
              {editMode ? (
                <textarea
                  value={editNote.text}
                  onChange={handleTextChange}
                  className="note-input"
                />
              ) : (
                <p>{popupNote.text}</p>
              )}
            </div>
            <div className="note-date">
              {new Date(popupNote.createdAt).toLocaleDateString()} {/* Дата теперь под кнопкой */}
            </div>
            <button onClick={() => setShowPopup(false)} className="popup-close">Закрыть</button>
            <button onClick={handleEditNote} className="popup-edit">Редактировать</button>
            {editMode && (
              <button onClick={handleSaveEdit} className="popup-save">Сохранить</button> // Кнопка для сохранения изменений
            )}
          </div>
        </div>
      )}

      {/* Отображаем заметки, если они есть */}
      <div className="notes-container">
        {sortedNotes.length === 0 && searchQuery && (
          <div className="no-results">Заметки не найдены</div>
        )}
        {sortedNotes.map((note, index) => (
          <div className="note" key={index}>
            <div className="note-header">
              <span>{note.topic} - {note.tag}</span>
              <button
                onClick={() => handleDeleteNote(index)}
                className="delete-note"
              >
                Удалить
              </button>
            </div>
            <p>{note.text.substring(0, 30)}...</p> {/* Ограничиваем длину текста */}
            <button
              onClick={() => handleOpenNote(note, index)} // Передаем индекс для редактирования
              className="view-note"
            >
              Смотреть
            </button>
            <div className="note-date">
              {new Date(note.createdAt).toLocaleDateString()} {/* Дата теперь под кнопкой */}
            </div>
          </div>
        ))}
      </div>

      {/* Календарь */}
      <div className="calendar-container">
        <Calendar />
      </div>
    </div>
  );
};

export default HomePage;
