import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { restoreNote, deleteNoteForever } from '../redux/slices/notesSlice';

const TrashPage = () => {
  const dispatch = useDispatch();
  const trashNotes = useSelector((state) => state.notes.trash);

  const handleRestore = (noteId) => {
    dispatch(restoreNote(noteId));
  };

  const handleDeleteForever = (noteId) => {
    dispatch(deleteNoteForever(noteId));
  };

  return (
    <div className="trash-page">
      <h2>Корзина</h2>
      <div className="trash-notes">
        {trashNotes.length === 0 ? (
          <p className="empty-trash">Корзина пуста.</p>
        ) : (
          trashNotes.map((note) => (
            <div key={note.id} className="trash-note">
              <h3>{note.title}</h3>
              <div className="trash-note-actions">
                <button className="restore-button" onClick={() => handleRestore(note.id)}>
                  Восстановить
                </button>
                <button className="delete-forever-button" onClick={() => handleDeleteForever(note.id)}>
                  Удалить навсегда
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default TrashPage;
