// src/pages/HomePage.js
import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css'; // Импортируем стили для календаря
import '../styles/HomePage.css'; // Подключаем стили для страницы

const HomePage = () => {
  const [notes, setNotes] = useState([]); // Состояние для заметок
  const [newNote, setNewNote] = useState({ text: '', topic: '', tag: '' }); // Состояние для новой заметки
  const [showPopup, setShowPopup] = useState(false); // Состояние для отображения попапа
  const [popupNote, setPopupNote] = useState(null); // Состояние для выбранной заметки в попапе
  const [searchQuery, setSearchQuery] = useState(''); // Состояние для строки поиска

  // Функция для добавления новой заметки
  const handleAddNote = () => {
    if (newNote.topic.trim() === '') return; // Проверка на пустую тему
    const newNoteWithDate = {
      ...newNote,
      createdAt: new Date().toISOString(), // Добавляем дату создания
    };
    setNotes([...notes, newNoteWithDate]);
    setNewNote({ text: '', topic: '', tag: '' }); // Очищаем поля после добавления заметки
  };

  // Функция для удаления заметки
  const handleDeleteNote = (index) => {
    setNotes(notes.filter((_, i) => i !== index));
  };

  // Открытие попапа с заметкой
  const handleOpenNote = (note) => {
    setPopupNote(note);
    setShowPopup(true);
  };

  // Функция для обработки изменения строки поиска
  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value.toLowerCase()); // Переводим строку в нижний регистр для удобства поиска
  };

  // Функция для сортировки заметок по дате (по убыванию)
  const sortNotesByDate = (notesArray) => {
    return notesArray.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
  };

  // Фильтруем заметки по строке поиска
  const filteredNotes = notes.filter((note) => {
    const searchInText = note.text.toLowerCase().includes(searchQuery);
    const searchInTopic = note.topic.toLowerCase().includes(searchQuery);
    const searchInTag = note.tag.toLowerCase().includes(searchQuery);
    return searchInText || searchInTopic || searchInTag; // Ищем по всем полям
  });

  // Сортируем заметки по дате
  const sortedNotes = sortNotesByDate(filteredNotes);

  return (
    <div className="home-page">
      {/* Если нет заметок, показываем сообщение */}
      {notes.length === 0 && (
        <div className="centered-text">Жду ваши заметки!</div>
      )}

      {/* Строка поиска */}
      <div className="search-bar">
        <input
          type="text"
          value={searchQuery}
          onChange={handleSearchChange}
          placeholder="Поиск по ключевым словам"
          className="search-input"
        />
      </div>

      {/* Форма для создания новой заметки */}
      <div className="note-form">
        <input
          type="text"
          value={newNote.topic}
          onChange={(e) => setNewNote({ ...newNote, topic: e.target.value })}
          placeholder="Тема заметки"
          className="note-input"
        />
        <input
          type="text"
          value={newNote.tag}
          onChange={(e) => setNewNote({ ...newNote, tag: e.target.value })}
          placeholder="Тег"
          className="note-input"
        />
        <textarea
          value={newNote.text}
          onChange={(e) => setNewNote({ ...newNote, text: e.target.value })}
          placeholder="Текст заметки"
          className="note-input"
        />
        <button onClick={handleAddNote} className="note-button">Записать</button>
      </div>

      {/* Попап для отображения текста заметки */}
      {showPopup && popupNote && (
        <div className="popup">
          <div className="popup-content">
            <div>
              <strong>Тема:</strong> {popupNote.topic}
            </div>
            <div>
              <strong>Теги:</strong> {popupNote.tag}
            </div>
            <div>
              <strong>Текст:</strong> <p>{popupNote.text}</p>
            </div>
            <button onClick={() => setShowPopup(false)} className="popup-close">Закрыть</button>
          </div>
        </div>
      )}

      {/* Отображаем заметки, если они есть */}
      <div className="notes-container">
        {sortedNotes.length === 0 && searchQuery && (
          <div className="no-results">Заметки не найдены</div>
        )}
        {sortedNotes.map((note, index) => (
          <div className="note" key={index}>
            <div className="note-header">
              <span>{note.topic} - {note.tag}</span>
              <span className="note-date">{new Date(note.createdAt).toLocaleDateString()}</span> {/* Отображаем дату */}
              <button
                onClick={() => handleDeleteNote(index)}
                className="delete-note"
              >
                Удалить
              </button>
            </div>
            <p>{note.text.substring(0, 30)}...</p> {/* Ограничиваем длину текста */}
            <button
              onClick={() => handleOpenNote(note)}
              className="view-note"
            >
              Смотреть
            </button>
          </div>
        ))}
      </div>

      {/* Календарь */}
      <div className="calendar-container">
        <Calendar />
      </div>
    </div>
  );
};

export default HomePage;
