// pages/NotesPage.js
import React from 'react';
import { useParams } from 'react-router-dom';

const NotesPage = () => {
  const { id } = useParams();
  // Здесь вы можете найти заметку по ID
  const note = JSON.parse(localStorage.getItem(id)); // Предположим, что заметки сохранены в локальном хранилище

  if (!note) {
    return <div>Заметка не найдена</div>;
  }

  return (
    <div className="notes-page">
      <h1>Заметка {note.id}</h1>
      <div className="note-details">
        <h2>{note.title}</h2>
        <p><strong>Теги:</strong> {note.tags.join(', ')}</p>
        <p><strong>Дата начала:</strong> {new Date(note.startDate).toLocaleString()}</p>
        <p><strong>Дата окончания:</strong> {new Date(note.endDate).toLocaleString()}</p>
      </div>
    </div>
  );
};

export default NotesPage;
