import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  notes: [], // Все заметки
  trash: [], // Заметки в корзине
};

const notesSlice = createSlice({
  name: 'notes',
  initialState,
  reducers: {
    addNote: (state, action) => {
      state.notes.push(action.payload);
    },
    deleteNote: (state, action) => {
      const noteId = action.payload;
      const note = state.notes.find((note) => note.id === noteId);
      if (note) {
        // Перемещаем заметку в корзину
        state.trash.push(note);
        state.notes = state.notes.filter((note) => note.id !== noteId);
      }
    },
    restoreNote: (state, action) => {
      const noteId = action.payload;
      const noteIndex = state.trash.findIndex((note) => note.id === noteId);
      if (noteIndex !== -1) {
        const note = state.trash[noteIndex];
        state.notes.push(note);
        state.trash.splice(noteIndex, 1);
      }
    },
    deleteNoteForever: (state, action) => {
      const noteId = action.payload;
      state.trash = state.trash.filter((note) => note.id !== noteId);
    },
  },
});

export const { addNote, deleteNote, restoreNote, deleteNoteForever } = notesSlice.actions;
export default notesSlice.reducer;
