import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import '../styles/HomePage.css';

const HomePage = () => {
  const [notes, setNotes] = useState([]); // Состояние для заметок
  const [newNote, setNewNote] = useState({ text: '', topic: '', tag: '' }); // Новая заметка
  const [popupNote, setPopupNote] = useState(null); // Текущая заметка для попапа

  // Добавление новой заметки
  const handleAddNote = () => {
    if (newNote.topic && newNote.text) {
      setNotes([...notes, newNote]);
      setNewNote({ text: '', topic: '', tag: '' });
    }
  };

  // Удаление заметки
  const handleDeleteNote = (index) => {
    setNotes(notes.filter((_, i) => i !== index));
  };

  // Открытие попапа с полной информацией о заметке
  const handleViewNote = (note) => {
    setPopupNote(note);
  };

  return (
    <div className="home-page">
      {/* Сообщение по умолчанию */}
      {notes.length === 0 && (
        <div className="centered-text">Жду ваши заметки!</div>
      )}

      {/* Форма для создания новой заметки */}
      <div className="note-form">
        <input
          type="text"
          value={newNote.topic}
          onChange={(e) => setNewNote({ ...newNote, topic: e.target.value })}
          placeholder="Тема заметки"
          className="note-input"
        />
        <input
          type="text"
          value={newNote.tag}
          onChange={(e) => setNewNote({ ...newNote, tag: e.target.value })}
          placeholder="Тег"
          className="note-input"
        />
        <textarea
          value={newNote.text}
          onChange={(e) => setNewNote({ ...newNote, text: e.target.value })}
          placeholder="Текст заметки"
          className="note-input"
        />
        <button onClick={handleAddNote} className="note-button">Записать</button>
      </div>

      {/* Попап с полной информацией */}
      {popupNote && (
        <div className="popup">
          <div className="popup-content">
            <h3>{popupNote.topic}</h3>
            <p><strong>Тег:</strong> {popupNote.tag}</p>
            <p>{popupNote.text}</p>
            <button onClick={() => setPopupNote(null)} className="popup-close">Закрыть</button>
          </div>
        </div>
      )}

      {/* Контейнер с заметками */}
      <div className="notes-container">
        {notes.map((note, index) => (
          <div className="note" key={index}>
            <div className="note-header">
              <span><strong>{note.topic}</strong> - {note.tag}</span>
              <button
                onClick={() => handleDeleteNote(index)}
                className="delete-note"
              >
                Удалить
              </button>
            </div>
            <button
              onClick={() => handleViewNote(note)}
              className="view-note"
            >
              Смотреть
            </button>
          </div>
        ))}
      </div>

      {/* Календарь */}
      <div className="calendar-container">
        <Calendar />
      </div>
    </div>
  );
};

export default HomePage;
