// src/pages/TrashPage.js
import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { restoreNote, deleteNoteForever } from '../redux/slices/notesSlice';
import '../styles/TrashPage.css';

const TrashPage = () => {
  const dispatch = useDispatch();
  const deletedNotes = useSelector((state) => state.notes.deletedNotes);

  const handleRestoreNote = (note) => {
    dispatch(restoreNote(note)); // Восстановление заметки
  };

  const handleDeleteForever = (noteId) => {
    dispatch(deleteNoteForever(noteId)); // Удаление заметки навсегда
  };

  return (
    <div className="trash-page">
      <h2>Корзина</h2>
      {deletedNotes.length === 0 ? (
        <div className="empty-trash">Корзина пуста</div>
      ) : (
        <div className="trash-notes">
          {deletedNotes.map((note) => (
            <div key={note.id} className="trash-note">
              <h3>{note.topic}</h3>
              <p>{note.text}</p>
              <div className="trash-note-actions">
                <button onClick={() => handleRestoreNote(note)} className="restore-button">
                  Восстановить
                </button>
                <button onClick={() => handleDeleteForever(note.id)} className="delete-forever-button">
                  Удалить навсегда
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default TrashPage;
