import React from 'react';

const NotesList = ({ notes, onDelete, onEdit }) => {
  return (
    <div className="notes-container">
      {notes.map((note) => (
        <div className="note" key={note.id}>
          <div className="note-header">
            <span>{note.topic} - {note.tag}</span>
            <button onClick={() => onDelete(note.id)} className="delete-note">Удалить</button>
            <button onClick={() => onEdit(note)} className="edit-note">Редактировать</button>
          </div>
          <p>{note.text.substring(0, 30)}...</p>
          <div className="note-date">
            {new Date(note.createdAt).toLocaleDateString()}
          </div>
        </div>
      ))}
    </div>
  );
};

export default NotesList;
