// pages/HomePage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './styles/HomePage.css';

const HomePage = () => {
  const [notes, setNotes] = useState([]);
  const [title, setTitle] = useState('');
  const [tags, setTags] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const navigate = useNavigate();

  const handleAddNote = (e) => {
    e.preventDefault();
    const newNote = {
      id: Date.now(),
      title,
      tags: tags.split(',').map(tag => tag.trim()),
      startDate,
      endDate,
    };
    setNotes((prevNotes) => [...prevNotes, newNote]);
    setTitle('');
    setTags('');
    setStartDate('');
    setEndDate('');
  };

  const handleViewNote = (id) => {
    navigate(`/note/${id}`);
  };

  return (
    <div className="home-page">
      <h1>Заметки</h1>
      {/* Форма для добавления заметки */}
      <form onSubmit={handleAddNote}>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Заголовок"
          required
        />
        <input
          type="text"
          value={tags}
          onChange={(e) => setTags(e.target.value)}
          placeholder="Теги"
        />
        <input
          type="datetime-local"
          value={startDate}
          onChange={(e) => setStartDate(e.target.value)}
          required
        />
        <input
          type="datetime-local"
          value={endDate}
          onChange={(e) => setEndDate(e.target.value)}
          required
        />
        <button type="submit">Добавить заметку</button>
      </form>

      {/* Список заметок */}
      <div className="notes-list">
        {notes.map((note) => (
          <div className="note" key={note.id}>
            <div className="note-header">
              <span>Заметка {note.id}</span>
              <button onClick={() => handleViewNote(note.id)}>Посмотреть</button>
            </div>
            <div className="note-body">
              <div className="note-title">{note.title}</div>
              <div className="note-tags">{note.tags.join(', ')}</div>
              <div className="note-dates">
                <p>Начало: {new Date(note.startDate).toLocaleString()}</p>
                <p>Окончание: {new Date(note.endDate).toLocaleString()}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
